sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        g_form.clearValue('u_pct_contact');
        g_form.clearValue('u_payable');
    }

    //Type appropriate comment here, and begin script below

    var glideAj = new GlideAjax('RetrieveProviderData');
    glideAj.addParam('sysparm_name', 'getData');
    glideAj.addParam('sysparm_contact', g_form.getValue('u_provider'));
    glideAj.addParam('sysparm_bank', g_form.getValue('u_provider'));
    glideAj.addParam('sysparm_account', g_form.getValue('u_provider'));
    glideAj.getXML(dataProvider);

    function dataProvider(response) {

        var dataFromScriptInclude = response.responseXML.documentElement.getAttribute('answer');

        //transform data from STRING into OBJECT

        var intoObj = JSON.parse(dataFromScriptInclude)
        g_form.setValue('u_pct_contact', intoObj.contact);
        g_form.setValue('u_payable', intoObj.bank + ' | ' + intoObj.account);

        /*
        g_form.addInfoMessage(typeof(dataFromScriptInclude));
        g_form.addInfoMessage('contents: ' + dataFromScriptInclude);
        g_form.addInfoMessage(typeof(JSON.parse(dataFromScriptInclude)));
        g_form.addInfoMessage('JSON Parse: ' + JSON.parse(dataFromScriptInclude));
        g_form.addInfoMessage('retrievable?: ' + intoObj.contact);
        */
    }



}
