sys_script_include.script:
var AdvancedRef = Class.create();
AdvancedRef.prototype = {
    initialize: function() {},

    getProviderItemsRefQualifer: function() {

        //gs.addInfoMessage('provider sys_id: '  + current.u_provider + ' provider name: ' + current.getDisplayValue('u_provider'));

        var prArr = [];

        var gr = new GlideRecord('x_58872_needit_item_catalog');
        gr.addQuery('u_itc_pct', current.u_provider);
        gr.query();

        while (gr.next()) {
			prArr.push(gr.sys_id.toString());

        }

        return 'sys_idIN' + prArr;


    },

    type: 'AdvancedRef'
};
