sys_script_include.script:
var RetrieveProviderData = Class.create();
RetrieveProviderData.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getData: function() {

		var dataObj = {};
        var provRec = new GlideRecord('x_58872_needit_provider_catalog');
		provRec.get(this.getParameter('sysparm_contact'));
        provRec.get(this.getParameter('sysparm_bank'));
        provRec.get(this.getParameter('sysparm_account'));

		//make property values strings by using + ''

		dataObj.contact = provRec.u_pct_contact + '';
		dataObj.bank = provRec.u_pct_bank.getDisplayValue() + '';
		dataObj.account = provRec.u_pct_acc + '';

		//gs.addErrorMessage('from SI object with JSON stringify: ' + JSON.stringify(dataObj));

		//convert OBJECT into STRING for client script retrieval

		return JSON.stringify(dataObj);

    },

    type: 'RetrieveProviderData'
});
